import api from "./api";
import axios from 'axios';

//calls the fetchContact API
export function fetchContact() {
    return (dispatch) => {
        axios.post(api+'/user/fetchContact')
        .then(response => {
            dispatch({ type: 'FETCH_CONTACTS', contacts:response.data});
          })
        .catch(function (error) {
            alert('Network Issue');
        })
    }

}
