import Header from '../Header/Header';
import axios from 'axios';

class EditContact extends React.Component {

    constructor(props) {
        super(props);
        this.state={
            view :[]
        }

    }

    componentDidMount(){
        const loggingIn = localStorage.getItem('user-token');
        if(!loggingIn){
          this.props.history.push("/");
