const initialState={
    email:'',
    password:''
}

const loginReducer =(state=initialState, action)=>{
    switch(action.type){
        case 'SET_EMAIL':{
            return {
                ...state,
                email:action.address,
            }
        }
        case 'SET_PASSWORD':{
            return {
                ...state,
                password:action.password,  
            }
        }

        default:
        return state;

    }
}
export default loginReducer;
