const initialState = {
    newUser: null,
    userDeleted: null,
    editCustomer: false,
    deliveryBoys: null

}
const manageDeliveryBoyReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'NEW_DELIVERY_BOY_CREATED': {
            return {
                ...state,
                newUser: action.status
            }
        }

        case 'DELETE_DELIVERY_BOY': {
            return {
                ...state,
                userDeleted: action.status
            }
        }

        case 'RESET_ACTION_VALUES': {
            return {
                ...state,
                newUser: null,
                userDeleted: null,
                editCustomer: false
            }
        }

        case 'UPDATE_DELIVERY_BOY': {
            return {
                ...state,
                editCustomer: action.status
            }
        }

        case 'DELIVERY_BOYS': {
            return {
                ...state,
                deliveryBoys: action.data
            }
        }
        default:
            return state;
    }
}
export default manageDeliveryBoyReducer;
