import api from "./api";

//calls the login API
export function login(email, password) {
    console.log(email);
    console.log(password);
    return (dispatch) => {
        fetch(
            api+'/user/login',
            {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    email: email,
                    password: password
                })
            }
        )
            .then(response => response.json())
            .then(responseData => {
                if (responseData.error) {
                    alert('Invalid Login Credentials, Check your email or password!!!!')
                } else {
                    const { token } = responseData.data;
                    const { userinfo } = responseData.data.userInfo.email
                    localStorage.setItem("user-token", token);
                    dispatch({ type: 'LOGIN_USER', userinfo })
                }
            })
            .catch(err => {
                alert('Network Issue');
            });
    }

}
