import React from 'react';
import { Link,withRouter } from 'react-router-dom';
import * as LoginAaction from '../../actions/loginAction';
import { connect } from 'react-redux';

class LoginPage extends React.Component {
    constructor(props) {
        super(props);

        // reset login status
        this.props.logout();

        this.state = {
            username: '',
            password: '',
            submitted: false,
            loggingIn:false
        };

    }
    componentDidMount(){
        console.log('this.props',this.props);
        if (localStorage.getItem('user-token')) {
            console.log('componentdidmount',this.props.loggingIn);
        }
    }

    handleChange = (e) => {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit = (e) => {
        e.preventDefault();
        this.setState({ submitted: true });
        const { username, password } = this.state;

        if (username && password) {
          const res = this.props.login(username, password);

          console.log('form is submitted',res);
        }
    }

    render() {
        const { loggingIn } = this.state;
        console.log('props----1',this.props.loggingIn);
        if(this.props.loggingIn){
            alert('great');
            this.props.history.push("/mycontacts");
         }
        const { username, password, submitted } = this.state;
        return (
            <div className="col-md-6 col-md-offset-3">
             <h2>Login</h2><pre>{
                 JSON.stringify(this.props.loggingIn,0,2)
             }</pre>
                <form name="form" onSubmit={this.handleSubmit}>
                    <div className={'form-group' + (submitted && !username ? ' has-error' : '')}>
                        <label htmlFor="username">Username</label>
                        <input type="text" className="form-control" name="username" value={username} onChange={this.handleChange} />
                        {submitted && !username &&
                            <div className="help-block">Username is required</div>
                        }
                    </div>
                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <label htmlFor="password">Password</label>
                        <input type="password" className="form-control" name="password" value={password} onChange={this.handleChange} />
                        {submitted && !password &&
                            <div className="help-block">Password is required</div>
                        }
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary">Login</button>
                        {loggingIn &&
                            <img src="data:image/gif;base64," />
                        }
                        <Link to="/register" className="btn btn-link">Register</Link>
                    </div>

                </form>

                </div>

        );
    }
}
const mapStateToProps = (state)=> {
    console.log('mapStateToProps',state);
    return {
        userinfo:state.loginReducer.currentUser,
        loggingIn:state.loginReducer.loggingIn 
    };
}

export default withRouter(connect(mapStateToProps, { ...LoginAaction })(LoginPage));
