const initialState = {
    currentUser: {},
    message:'',
    error:''
}
if (localStorage.getItem('user-token')) {
    initialState.loggedIn = true;
}

const loginReducer =(state=initialState, action)=>{
    switch(action.type){
        case 'LOGIN_USER':{
            return {
                  ...state, currentUser: action.userinfo
            }
        }
        case 'LOGIN_SUCCESS':{
            return {
                  ...state, message: action.userinfo
            }
        }
        case 'LOGIN_FAILURE':{
            return {
                  ...state, message: action.userinfo
            }
        }
        case 'LOGOUT_USER':
        return {...state, currentUser: {} }
        default:
        return state;

    }
}
export default loginReducer;
