import api from "./api";
import { history } from '../_helpers/history';

//calls the fetchContact API
export function fetchContact() {

    // return dispatch => {
    //     fetch(' http://localhost:7000/api/user/fetchContact').then(res => res.json())
    //     .then(contacts => {
    //         console.log('respone data',contacts);
    //       dispatch({type:"FETCH_CONTACTS",contacts});
    //     }).catch(err=>{
    //        alert('Network issue');
    //     });
    //     }

    return (dispatch) => {

        fetch(
            api+'/user/fetchContact',
            {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': "Bearer "+ localStorage.getItem('user-token')
                },
            }
        )
            .then(response => response.json())
            .then(responseData => {
                  
                  if (responseData.error) {
                    const {error} = responseData;
                    dispatch({ type: 'CONTACT_FAILURE', error });
                
                } else {
                    const {contacts} = responseData;
                       dispatch({ type: 'FETCH_CONTACTS', contacts });

                }
            })
            .catch(err => {
                alert('Network Issue');
            });
    }

}
